    {
        int d = Param.Number_of_days;
        childMouse = e.ClientX;
        //JSRuntime.InvokeVoidAsync("console.log", "Data line mouse move => " + dataInt.ToString());
        if (IsLeft == 2)
        {
            if (StartX == -1)
            {
