    string format = "00:00";
    string Format { get => format; set { format = value; InvokeAsync(StateHasChanged); } }

    protected override void OnInitialized()
    {
        SetUpValue();
        base.OnInitialized();
    }

    protected override void OnParametersSet()
    {
        SetUpValue();
