    {
        try
        {
            if (data.SitenCdSeq == BranchSeq && data.TokuiSeq == TokuiSeq)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        catch (Exception ex)
        {
            return (data.SitenCdSeq == BranchSeq && data.TokuiSeq == TokuiSeq);
            errorModalService.ShowErrorPopup(Lang["Error"], ex.GetOriginalException()?.Message);
        }

    }

