            LastYearSaleData = LastYearSaleData.FindAll(data => data.Time.Month != 2 || data.Time.Day != 29);
        }
        catch (Exception ex)
        {
            errorModalService.ShowErrorPopup(Lang["Error"], ex.GetOriginalException()?.Message);
        }

    }

