            reportCondition.VehicleDispatchOffice1 = departureOffice ?? new DepartureOfficeData();
            //reportCondition.VehicleDispatchOffice2 = departureOffice ?? new DepartureOfficeData();
            CheckSelectedVehicleDispatchToAll = true;
