        {
            NavigationManager.NavigateTo(string.Format("bookinginput?motoTenantCdSeq={0}&motoUkeNo={1}&motoUnkRen={2}&motoYouTblSeq={3}",item.MotoTenantCdSeq,item.MotoUkeNo,item.MotoUnKRen,item.MotoYouTblSeq));
        }
        StateHasChanged();
    }
