                Summary.TotalPreviousReceiveAmount = 0;
                Summary.TotalTransferFee = 0;
                btnReportActive = false;
            }
            await Task.Run(() =>
            {
                isLoading = false;
                InvokeAsync(StateHasChanged).Wait();
            });
        }
        catch (Exception ex)
        {
