    {
        Width = (double)await BrowserResizeService.GetInnerWidthStaff();
        StateHasChanged();
    }

    private async Task BrowserHasCallbackWidth()
    {
        Width = (double)await BrowserResizeService.GetCallbackInnerWidthStaff();
        StateHasChanged();
    }

