    [CascadingParameter] StaffContainer Container { get; set; }
    [Parameter] public int ID { get; set; }
    List<JobData> ListJobs = new List<JobData>();
    double height = 0;
    double heightline = 2;

    protected override void OnParametersSet()
    {
        ListJobs.Clear();
        ReformatRenderItem();
    }

    private void ReformatRenderItem()
    {
        if (Container.Params.View == (int)ViewMode.Large)
        {
            heightline = 2; // 32px
        }
        if (Container.Params.View == (int)ViewMode.Medium)
        {
            heightline = 1.0625; // 17px
        }
        if (Container.Params.View == (int)ViewMode.Small)
        {
            heightline = 0.875; // 14px
        }

        height = 0;
        List<JobData> tmp = new List<JobData>();
        tmp.AddRange(Container.Jobs.Where(x => x.JobID == ID));

        if (tmp.Count > 0)
        {
            List<List<JobData>> myList = new List<List<JobData>>();

            foreach (JobData job in tmp)
            {
                var rowPosition = arrangePositionTmp(job, myList);

                if (myList.Any())
                {
                    if (rowPosition >= myList.Count - 1)
                    {
                        myList.Add(new List<JobData>());
                    }
                    if (myList[rowPosition].Any())
                    {
                        myList[rowPosition].Add(job);
                    }
                    else
                    {
                        myList[rowPosition].Add(job);
                    }
                }
                else
                {
                    myList.Add(new List<JobData>());
                    myList[rowPosition].Add(job);
                }
            }

            for (var i = 0; i < myList.Count; i++)
            {
                if (myList[i].Count() < 1)
                {
                    myList.RemoveAt(i);
                }
            }

            for (var i = 0; i < myList.Count; i++)
            {
                for (var j = 0; j < myList[i].Count; j++)
                {
                    if (i == 0)
                    {
                        JobData tmpV = myList[i][j];
                        tmpV.Width = BusScheduleHelper.calwidth(Container.Width, tmpV.StartDate, tmpV.TimeStart.ToString("D4"), tmpV.EndDate, tmpV.TimeEnd.ToString("D4"), 1);
                        tmpV.Left = BusScheduleHelper.calleft(Container.Width, tmpV.StartDate, tmpV.TimeStart.ToString("D4"), 1, Container.Date);
                        tmpV.Top = 0.3125; //5px
                        tmpV.Height = heightline;
                        tmpV.CCSStyle = StyleItemText(tmpV);
                        ListJobs.Add(tmpV);
                        continue;
                    }
                    else
                    {
                        JobData tmpV = myList[i][j];
                        tmpV.Width = BusScheduleHelper.calwidth(Container.Width, tmpV.StartDate, tmpV.TimeStart.ToString("D4"), tmpV.EndDate, tmpV.TimeEnd.ToString("D4"), 1);
                        tmpV.Left = BusScheduleHelper.calleft(Container.Width, tmpV.StartDate, tmpV.TimeStart.ToString("D4"), 1, Container.Date);
                        tmpV.Top = heightline * i + 0.3125 + (i) * 0.3125;
                        tmpV.Height = heightline;
                        tmpV.CCSStyle = StyleItemText(tmpV);
                        ListJobs.Add(tmpV);
                        continue;
                    }
                }
            }

            if (myList.Count() == 1)
            {
                height = heightline + 0.625;
            }
            else
            {
                height = heightline * (myList.Count()) + (myList.Count() - 1) * 0.3125 + 0.625;
            }

            for (var i = 0; i < Container.Staffs.Count(); i++)
            {
                if (Container.Staffs[i].SyainCdSeq == ID)
                {
                    Container.Staffs[i].Height = height;
                }
            }
        }
    }

    private int arrangePositionTmp(JobData tmp, List<List<JobData>> itemList)
    {
        if (itemList.Count <= 0)
        {
            return 0;
        }

        for (var i = 0; i < itemList.Count; i++)
        {
            if (itemList[i].Count == 0)
            {
                return i;
            }

            if (itemList[i][itemList[i].Count - 1].TimeEndString <= tmp.TimeStartString)
            {
                return i;
            }
        }

        return itemList.Count;
    }

    private string StyleItemText(JobData item)
    {
        return "width:" + item.Width.ToString() + "px;top:" + item.Top.ToString() + "rem;left:" + item.Left.ToString() + "px;";
    }

    private async Task HandleDrop()
    {
        if (Container.Job == null || Container.Job.JobID == ID)
        {
            return;
        }
        await Container.SwapJobAsync(ID);
    }

