    public async Task UpdateJobAsync(StaffData newStaff) {
        var task = Staffs.SingleOrDefault(x => x.SyainCdSeq == newStaff.SyainCdSeq);

        if (task != null)
        {
            Payload = newStaff;

            if(isHoliday && Payload.HolidayID == 0)
            {
                Payload.HolidayID = Holiday.HolidayID;
                Payload.HolidayNm = Holiday.HolidayNm;
                Payload.DayOff = Date.ToString("yyyyMMdd");
                Payload.Status = Flag.Absense;
                Holiday = new HolidayData();
                isHoliday = false;
            }
            if(isWork && Payload.HolidayID != 0)
            {
                Payload.WorkID = Work.WorkID;
                Payload.WorkNm = Work.WorkNm;
                Payload.Status = Flag.NotTodo;
                Work = new WorkData();
                isWork = false;
            }
            if (isJob) {
                Payload.JobID = Job.JobID;
                Payload.Status = Flag.Todo;
                Job = new JobData();
                isJob = false;
            }

            StaffData tmp = new StaffData();
            tmp = Payload;
            Payload = new StaffData();
            await OnStatusUpdated.InvokeAsync(tmp);
        }
    }

    public async Task SwapJobAsync(int jobID)
    {
        var task = Jobs.SingleOrDefault(x => x.JobID == jobID);

        if(task != null)
        {
            int id = task.JobID;
            task.JobID = Job.JobID;
            Job.JobID = id;
            JobData tmp = new JobData();
            tmp = Job;
            Job = new JobData();
            await OnStatusSwaped.InvokeAsync(tmp);
        }
    }

