    #region Component Lifecycle
    /// <summary>
    /// Load javascript of page
    /// </summary>
    /// <returns></returns>
    protected override void OnParametersSet()
    {
        // 乗務員並び
        if (Container.Param.OrderbyList == (int)Staffs_OrderbyList.DutyEmployee)
        {
            StaffsName = StaffsName.OrderBy(x => x.SyokumuCdSeq).ThenBy(x => x.SyainCdSeq).ToList();
        }
        if (Container.Param.OrderbyList == (int)Staffs_OrderbyList.BranchEmployee)
        {
            StaffsName = StaffsName.OrderBy(x => x.EigyoCdSeq).ThenBy(x => x.SyainCdSeq).ToList();
        }
        if (Container.Param.OrderbyList == (int)Staffs_OrderbyList.BranchJob)
        {
            StaffsName = StaffsName.OrderBy(x => x.EigyoCdSeq).ThenBy(x => x.SyokumuCdSeq).ToList();
        }
        if (Container.Param.OrderbyList == (int)Staffs_OrderbyList.Rolling)
        {
            StaffsName = StaffsName.OrderBy(x => x.TenkoNo).ToList();
        }

        // 乗務員表示名順
        foreach (var i in StaffsName)
        {
            if (Container.Param.OrderbyName == (int)Staffs_OrderbyName.DutyEmployee)
            {
                i.StaffName = i.SyokumuNm + "・" + i.SyainNm;
            }
            if (Container.Param.OrderbyName == (int)Staffs_OrderbyName.BranchDuty)
            {
                i.StaffName = i.RyakuNm + "・" + i.SyokumuNm;
            }
            if (Container.Param.OrderbyName == (int)Staffs_OrderbyName.BranchEmployee)
            {
                i.StaffName = i.RyakuNm + "・" + i.SyainNm;
            }
        }

        // 職務
        if (Container.Param.Duties == (int)Staffs_Duties.All)
        {
            StaffsName = StaffsName.ToList();
        }
        if (Container.Param.Duties == (int)Staffs_Duties.Driver)
        {
            StaffsName = StaffsName.Where(x => x.SyokumuCdSeq < 3).ToList();
        }
        if (Container.Param.Duties == (int)Staffs_Duties.Guide)
        {
            StaffsName = StaffsName.Where(x => x.SyokumuCdSeq == 3 || x.SyokumuCdSeq == 4).ToList();
        }
        if (Container.Param.Duties == (int)Staffs_Duties.OfficeWork)
        {
            StaffsName = StaffsName.Where(x => x.SyokumuCdSeq > 4).ToList();
        }
    }
    #endregion


    /// <summary>
    /// Get color
    /// </summary>
    /// <param name="date"></param>
    /// <returns></returns>
    public string GetheaderColor(string date)
    {
        if (Calendlst.Count != 0 && Calendlst.Where(t => t.CompanyCdSeq == 1 && t.CalenSyu == 1 && t.CalenYmd == date).Any())
        {
            string commentdate = Calendlst.Where(t => t.CompanyCdSeq == 1 && t.CalenSyu == 1 && t.CalenYmd == date).FirstOrDefault().CalenCom;
            if (commentdate != "" && commentdate.Length <= 100)
            {
                color = "#8533ff";
            }
            else
            {
                color = "#000";
            }
        }
        else
        {
            color = "#000";
        }
        return color;
    }

